import React, { useState, useEffect } from "react";
import styled from "styled-components";
import App from "./App";

const Square = ({ color, piece = {}, onClick }) => {
  let pieceChar;
  switch (piece.type) {
    case "rook":
      pieceChar = "♖";
      break;
    case "knight":
      pieceChar = "♘";
      break;
    case "bishop":
      pieceChar = "♗";
      break;
    case "king":
      pieceChar = "♕";
      break;
    case "queen":
      pieceChar = "♔";
      break;
    case "pawn":
      pieceChar = "♙";
      break;
  }

  const SquareDiv = styled.div`
  border: 1.2px solid black;
  height: 70px;
  width: 70px;
  font-size: 45px;
  display: flex;
  align-items: center;
  justify-content: center;
  color: ${(props) => (props.color === "white" ? "gray" : "blue")};
  /* color: ${(props) => (props.piece ? "green" : "blue")}; */
  &:hover {
      border: 1.2px solid red;
      color: green;
    }
    `;
  console.log(piece);
  //   console.log(colorPic);
  //   console.log(color);
  return (
    <SquareDiv
      color={color}
      piece={piece}
      onClick={onClick}
      // onMouseOver={onMouseOver}
      style={{
        // color: piece.player === "white" ? "green" : "blue",
        backgroundColor: color === "black" ? "white" : "gray",
        // height: "70px",
        // width: "70px",
        // fontSize: "45px",
        // display: "flex",
        // alignItems: "center",
        // justifyContent: "center",
      }}
    >
      {pieceChar}
    </SquareDiv>
  );
};

export default Square;
