import React, { useState, useEffect } from "react";
import styled from "styled-components";

const SquareDiv = styled.div`
  border: 1.2px solid black;
  height: 70px;
  width: 70px;
  font-size: 45px;
  display: flex;
  align-items: center;
  justify-content: center;
  color: ${"piece.pler" === "white" ? "green" : "blue"};
  &:hover {
    border: 1.2px solid red;
  }
`;

const Square = ({ color, piece = {}, onClick, onMouseOver }) => {
  let pieceChar;
  switch (piece.type) {
    case "rook":
      pieceChar = "♖";
      break;
    case "knight":
      pieceChar = "♘";
      break;
    case "bishop":
      pieceChar = "♗";
      break;
    case "king":
      pieceChar = "♕";
      break;
    case "queen":
      pieceChar = "♔";
      break;
    case "pawn":
      pieceChar = "♙";
      break;
  }
  return (
    <SquareDiv
      onClick={onClick}
      //   onMouseOver={onMouseOver}
      style={{
        // color: piece.player === "white" ? "green" : "blue",
        backgroundColor: color === "black" ? "white" : "gray",
        // height: "70px",
        // width: "70px",
        // fontSize: "45px",
        // display: "flex",
        // alignItems: "center",
        // justifyContent: "center",
      }}
    >
      {pieceChar}
    </SquareDiv>
  );
};

export default Square;
