import React from 'react';
import logo from './logo.svg';
import './App.css';

const Square = ({ color, piece }) => {
  return <div style={{color: piece.player === "white"? "white" : "blue", border:"black solid 1px", height:"100px", width:"100px", backgroundColor: color === "black"? "white" : "gray", fontSize:"10px", display:"flex", alignItems:"center", justifyContent:"center"}}>

         {piece.type}

         </div>
}
const Board = () => {
  return <div>
      <div style={{display:"flex", flexDirection:"row"}}><Square color="white" piece={{type:"rock", player:"white"}}/>
           <Square color="black" piece={{type:"knight", player:"black"}} />
           <Square color="white" piece={{type:"bishop", player:"white"}} />
           <Square color="black" piece={{type:"queen", player:"white"}} />
           <Square color="white" piece={{type:"king", player:"white"}} />
           <Square color="black" piece={{type:"bishop", player:"white"}} />
           <Square color="white" piece={{type:"knight", player:"white"}} />
           <Square color="black" piece={{type:"rock", player:"white"}} />
           </div>
           <div style={{display:"flex", flexDirection:"row"}}><Square color="black" piece={{type:"rock", player:"black"}} />
           <Square color="white" piece={{type:"knight", player:"black"}} />
           <Square color="black" piece={{type:"bishop", player:"black"}} />
           <Square color="white" piece={{type:"king", player:"black"}} />
           <Square color="black" piece={{type:"queen", player:"black"}} />
           <Square color="white" piece={{type:"bishop", player:"black"}} />
           <Square color="black" piece={{type:"knight", player:"black"}} />
           <Square color="white" piece={{type:"rock", player:"black"}} />
           </div>

  </div>
}

function App() {
  return (
    <div className="App">
      <Board />
      {/* <Square color="white" piece={{type:"rock", player:"black"}} /> */}
    </div>
  );
}

export default App;
