import React from 'react';
import logo from './logo.svg';
import './App.css';

const Square = ({ color, piece }) => {
  return <div style={{border:"3px,black,solid", height:"100px", width:"100px", backgroundColor:color, fontSize:"10px", display:"flex", alignItems:"center", justifyContent:"center"}}>

         <h1>{piece}</h1>
         </div>
}
const Board = () => {
  return <div>
      <div style={{display:"flex", flexDirection:"row"}}><Square color="white" piece="rock"/>
           <Square color="gray" piece="rock"/></div>
           <div style={{display:"flex", flexDirection:"row"}}><Square color="gray" piece="rock"/>
           <Square color="white" piece="rock"/></div>

  </div>
}

function App() {
  return (
    <div className="App">
      <Board />
    </div>
  );
}

export default App;
