        color: piece.player === "white" ? "green" : "blue",
        border: "black solid 1px",
        height: "70px",
        width: "70px",
        backgroundColor: color === "black" ? "white" : "gray",
        fontSize: "45px",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
