import React from "react";
import logo from "./logo.svg";
import "./App.css";

const mapObj = {
  "00": "rook",
  "27": "queen"
}

const Square = ({ color, piece = {} }) => {
  let pieceChar;
  switch (piece.type) {
    case "rook":
      pieceChar = "♖";
      break;
    case "knight":
      pieceChar = "♘";
      break;
    case "bishop":
      pieceChar = "♗";
      break;
    case "king":
      pieceChar = "♕";
      break;
    case "queen":
      pieceChar = "♔";
      break;
    case "pawn":
      pieceChar = "♙";
      break;
  }
  return (
    <div
      style={{
        color: piece.player === "white" ? "green" : "blue",
        border: "black solid 1px",
        height: "70px",
        width: "70px",
        backgroundColor: color === "black" ? "white" : "gray",
        fontSize: "45px",
        display: "flex",
        alignItems: "center",
        justifyContent: "center"
      }}
    >
      {pieceChar}

    </div>
  );
};
